mapper.select:
SELECT MAX(views.filed0) as filed0, MAX(views.id0) as id0, MAX(views.filed1) as filed1, MAX(views.id1) as id1, MAX(views.filed2) as filed2, MAX(views.id2) as id2, MAX(views.copoleCode) as copoleCode, MAX(views.ipType) as ipType, MAX(views.model) as model, COUNT(views.three) as zeroToThree, COUNT(views.five) as threeToFive, COUNT(views.ten) as fiveToTen, COUNT(views.unknown) as unknown, MAX(views.shelveDate) as shelveDate, MAX(views.shelveName) as shelveName, MAX(views.shelveId) as shelveId, MAX(views.id) as id FROM ( SELECT tmp.filed0, tmp.id0, tmp.filed1, tmp.id1, tmp.filed2, tmp.id2, tmp.copoleCode, tmp.ipType, tmp.model, tmp.ip, total.three, total.five, total.ten, CASE WHEN total.three IS NULL AND total.five IS NULL AND total.ten IS NULL THEN 'unknown' END AS UNKNOWN, total.shelveDate, case when total.shelveName is null then '-' ELSE total.shelveName end as shelveName, total.shelveId, total.id from tbl_ipc_ip_tmp tmp LEFT JOIN (SELECT b.id, b.shelveDate, b.shelveId, b.shelveName, CASE WHEN b.da >= #{zero} AND b.da < #{three} THEN 'three' END AS three, CASE WHEN b.da >= #{three} AND b.da < #{five} THEN 'five' END AS five, CASE WHEN b.da >= #{five} THEN 'ten' END AS ten FROM ( SELECT a.id, a.shelveDate, a.shelveId, a.shelveName, TIMESTAMPDIFF( YEAR, shelveDate, NOW()) AS da FROM ( SELECT con1.id, con1.shelveId, con2.shelveDate, con3.shelveName FROM ( SELECT id, value AS shelveId, '' AS shelveDate FROM tbl_dossier_content con WHERE type_id IN(#{id},#{id1}) ) con1 LEFT JOIN ( SELECT id, value AS shelveDate FROM tbl_dossier_content con WHERE type_id IN (#{time},#{time1})) con2 ON con1.id = con2.id LEFT JOIN ( SELECT id, value AS shelveName FROM tbl_dossier_content con WHERE type_id IN (#{name},#{name1}) ) con3 ON con1.id = con3.id) a LEFT JOIN tbl_dossier_content_base base ON a.id = base.id where base.dossiers_status = #{dossierStatus} ) b) total ON tmp.cameraId = total.shelveId) views
1=1
and model in
#{item}
and ipType in
#{item}
and shelveName in
#{item}
and ${ida} in
#{item}
and views.ip in
#{ip}
group by
${item}

update.foreach:
update tbl_ipc_extra set
cameraType=#{cameraInformationVo.cameraType},
cameraValue=#{cameraInformationVo.cameraValue},
isUse=#{cameraInformationVo.isUse},
modifyTime=#{cameraInformationVo.updateTime}
where cameraType = #{cameraInformationVo.cameraType} and cameraSn = #{cameraInformationVo.cameraSn}
